mapper.select:
SELECT * FROM ( SELECT rownum rnum, B.* FROM (

)
B ORDER BY rnum DESC ) R WHERE R.rnum BETWEEN #{startNo } AND #{endNo } ORDER BY rnum

SELECT COUNT(*) CNT FROM ( SELECT b.findNo , a.bandName , b.title , b.content , b.writtendate , B.positionNo , CASE WHEN b.positionNo = '1' THEN '보컬' WHEN B.positionNo = '2' THEN '기타' WHEN B.positionNo = '3' THEN '드러머' ELSE '콜라보' END position , b.adsno , CASE WHEN B.adsNo = '1' THEN '맴버' ELSE '콜라보' END ads , a.profileNo , c.profileNo , c.path , c.ORIGINNAME , c.STOREDNAME FROM BAND A , FINDMEMBER B , ProfilePic c WHERE a.bandNo = b.bandNo AND a.profileNo = c.profileNo
AND b.adsNo = #{adsNo}
) CNT

SELECT profileNo
FROM band
WHERE bandNo = #{bandNo}

SELECT profileNo, path, originName, storedName
FROM profilePic
WHERE profileNo = #{profileNo }

SELECT bandMemberNo, bandMemName, mPosition, bandNo
FROM bandMember
WHERE bandNo = #{bandNo}

SELECT genreNo , genreName
FROM Genre
WHERE genreNo = #{genreNo}

SELECT bandNo , bandName
FROM band
WHERE bandNo = #{bandNo}

SELECT userId , bandName ,bandNo
FROM band
WHERE bandNo = #{bandNo}

SELECT userId , contact , userName
FROM Member
WHERE userId = #{userId}

mapper.update:
UPDATE findmember SET positionNo = #{positionNo} 
					, content=#{content} , title=#{title} , genre=#{genre},
					adsNo=#{adsNo}
					WHERE findNo = #{findNo} AND bandNo=#{bandNo}
