mapper.select:
SELECT COUNT(*) CNT
FROM member
WHERE 1=1

	
		AND userId=#{userId }
	
	
		AND password=#{password }

SELECT userId
FROM member
WHERE email=#{email }

SELECT password
FROM member
WHERE email=#{email }
	AND userId=#{userId }

SELECT roleId
FROM member
WHERE userId=#{userId }
	AND password=#{password }
