mapper.select:
select h.* from resumes r, history h
where
	r.resumesNo = h.resumesNo
	and r.bandNo=#{bandNo }
	and r.resumesNo=#{resumesNo }
order by year

mapper.insert:

	select * from (
	select NVL(max(musicNo)+1, 1) from Music
	)

insert into Music(
		musicNo
		, bandNo
		, musicTitle
		, path
		, writtenDate
	)
	values(
		#{musicNo }
		, #{bandNo }
		, #{musicTitle }
		, #{path }
		, sysdate
	)



	select * from (
	select NVL(max(historyNo)+1, 1) from History
	order by historyNo desc
	)
	where rownum = 1


insert into History
	values(
	#{historyNo }
	, #{resumesNo }
	, 0
	, ''
	)

mapper.delete:
	delete History
	where historyNo = (
		select * from (
			select historyNo from History
            where resumesNo = #{resumesNo }
			order by historyNo desc
			)
		where rownum = 1
		)

mapper.update:
update resumes set
	resumesTitle=#{resumesTitle }
	, bandInfo=#{bandInfo }
	, musicNo=#{musicNo }
where resumesNo = #{resumesNo }

mapper.select:
select *
from bar 
where barno = #{barNo}

mapper.update:
update bandGenre set
	genreNo=#{genreNo }
where bandNo = #{bandNo } and resumesNo = #{resumesNo }

mapper.select:
SELECT * FROM (
	SELECT rownum rnum, B.* FROM (
		SELECT * FROM Application
		where barName= ( select barName from bar where userid=#{member.userId } )
		ORDER BY appno DESC
		) B
	ORDER BY rnum
)
WHERE rnum between #{paging.startNo } AND
#{paging.endNo }

select count(*) from Application
WHERE barName=#{bar.barName }
	and appDate between #{startDate } and #{endDate }

SELECT * FROM (
SELECT rownum rnum, B.* FROM (
SELECT * FROM Application
	where appDate between #{startDate } and #{endDate }
		and barName=#{bar.barName }
ORDER BY appno DESC
) B
ORDER BY appno
)
WHERE
	rnum between #{paging.startNo } AND #{paging.endNo }

mapper.update:
update Bar set
locationNo=#{locationNo }
, barAddress=#{barAddress }
, barInfo=#{barInfo }
, genreNo=#{genreNo }
