mapper.select:
SELECT * FROM (
SELECT rownum rnum, B.* FROM (
SELECT * FROM member
ORDER BY userId DESC
) B
ORDER BY rnum
)
WHERE rnum between #{startNo } AND
#{endNo }

SELECT * FROM (
 SELECT rownum rnum, B.* FROM (
 	SELECT * FROM member
	WHERE userId LIKE '%'||#{search }||'%'
 	ORDER BY userId DESC
 ) B
 ORDER BY rnum
)
WHERE rnum between #{startNo } AND
#{endNo }

SELECT * FROM (
SELECT rownum rnum, B.* FROM (
SELECT * FROM resume
ORDER BY resumeNo DESC
) B
ORDER BY rnum
)
WHERE rnum between #{startNo } AND
#{endNo }

SELECT * FROM (
 SELECT rownum rnum, B.* FROM (
 	SELECT * FROM resume
	WHERE resumeNo LIKE '%'||#{search }||'%'
 	ORDER BY resumeNo DESC
 ) B
 ORDER BY rnum
)
WHERE rnum between #{startNo } AND
#{endNo }
